mapper.update:
UPDATE customer
SET customer_pw = PASSWORD(#{customerPw})
WHERE customer_no = #{customerNo}

mapper.select:
SELECT customer_phone AS customerPhone
FROM customer_detail
WHERE customer_no = #{customerNo}

SELECT customer_no AS customerNo, customer_img_no AS customerImgNo, customer_img_file_name AS customerImgFileName
FROM customer_img
WHERE customer_no = #{customerNo}
