mapper.update:
UPDATE sports_equipment_order
SET order_status = #{changeOrderStatus}
WHERE order_no = #{orderNo}

mapper.select:
SELECT seo.order_no orderNo, seo.employee_orderer employeeOrderer, oed.employee_name employeeOrdererName, seo.branch_no branchNo, bch.branch_name branchName, seo.sports_equipment_no sportsEquipmentNo, se.item_name itemName, seo.quantity, se.item_price itemPrice, (seo.quantity * se.item_price) totalPrice, seo.employee_approver employeeApprover, aed.employee_name employeeApproverName, seo.order_status orderStatus, seo.createdate FROM sports_equipment_order seo LEFT JOIN employee_detail oed ON seo.employee_orderer = oed.employee_no LEFT JOIN branch bch ON seo.branch_no = bch.branch_no LEFT JOIN sports_equipment se ON seo.sports_equipment_no = se.sports_equipment_no LEFT JOIN employee_detail aed ON seo.employee_approver = aed.employee_no
AND seo.branch_no = #{branchNo} AND seo.order_status = '대기'
ORDER BY seo.createdate DESC LIMIT #{beginRow}, #{rowPerPage}
